            new("Spawn Archdemon", "spawnenemy_xlarchdemon") { Category = "Spawn Enemy", Description = "Big. Red. Horny. It really doesn’t get much more demonic than that. Arch Demons strive to exemplify all that it means to be a demon, and they’re diabolically good at it. They swear a lot, too." },            new("Spawn Archon Ascia", "spawnenemy_armor_6") { Category = "Spawn Enemy", Description = "The least fancy of all Archons, but perhaps the most effective. Marching methodically into battle with a big axe and shield might be boring, but it gets the job done." },            new("Spawn Archon Disco", "spawnenemy_xlarmor2") { Category = "Spawn Enemy", Description = "A deluxe model Archon featuring the exact same stats at twice the price. Sold as a luxury defense option, its sheer size and impressive strength do much to hide the fact it’s mostly made of recycled infantry helmets and old buckets." },            new("Spawn Archon Fiamma", "spawnenemy_armor_fire") { Category = "Spawn Enemy", Description = "An automated soldier with a tendency to overheat and catch fire. Rather than fix the problem, Gallo Tower claimed it was a feature instead of a bug, and sold these flame-wreathed safety violations to a cabal of demons who gave them great user reviews." },            new("Spawn Archon Lancia", "spawnenemy_armorspear_6") { Category = "Spawn Enemy", Description = "An Archon model designed to respond to a wide variety of commands, provided those commands involve stabbing something with a long sharp stick." },            new("Spawn Archon Oro", "spawnenemy_xlarmor_gold") { Category = "Spawn Enemy", Description = "Gilded automatons drafted into service by the Most Holy Order to keep the peace. They didn’t so much keep the peace as keep breaking all the fancy stained glass with their mindless lumbering. They’re as expensive as they look." },            new("Spawn Archon Rame", "spawnenemy_xlarmor_green") { Category = "Spawn Enemy", Description = "“When in doubt, make the sword bigger.” This design philosophy was key to the creation of the Rame, which also boasts the upper body strength required to lift said sword more than a foot off the ground." },            new("Spawn Archon Spada", "spawnenemy_xlarmor1") { Category = "Spawn Enemy", Description = "The twisted result of science and sorcery, these animated suits of armor were mass produced at Gallo Tower as one of its most profitable creations. The sword’s size is terrifying, but woefully impractical, rendering it too heavy to be swung. That hardly matters when it can still mash you like some sort of genocidal butter churner." },            new("Spawn Armor 1", "spawnenemy_armor1") { Category = "Spawn Enemy" },             new("Spawn Armor Sword", "spawnenemy_armor_sword") { Category = "Spawn Enemy" },             new("Spawn Batswarm", "spawnenemy_batswarm") { Category = "Spawn Enemy" },             new("Spawn Big Golem", "spawnenemy_xlgolem1") { Category = "Spawn Enemy", Description = "Big, Big Golem! Put it together, and what do you get? The Big Big Golem Construction Set! Load it up! Truck along! Dump it out! Big Big Golem!" },            new("Spawn Big Golem 2", "spawnenemy_xlgolem2") { Category = "Spawn Enemy" },             new("Spawn Big Mummy", "spawnenemy_xlmummy") { Category = "Spawn Enemy", Description = "Despite appearances, these shambling cadavers change their bandages daily. Whatever’s going on underneath all those wrappings remains a mystery, but one presumes it’s thoroughly disgusting." },            new("Spawn Bloodbath", "spawnenemy_bat6") { Category = "Spawn Enemy", Description = "Foul ichor seems to ooze from every pore of this creature’s slick skin, leading many to believe that it bathes in the blood of the freshly slain. Closer study reveals their fur is simply red, however. They’re bats, and nothing more." },            new("Spawn Bullet 1", "spawnenemy_bullet_1") { Category = "Spawn Enemy" },             new("Spawn Bullet 2", "spawnenemy_bull